import React, { Component } from 'react';
import LinkedIn from "../images/LinkedIn-Logos/LI-In-Bug.png";
import Email from "../images/email_logo.png";
import AAPS from "../images/AAPS_RGB_Strap.png";
import ORCID from "../images/ORCIDiD_icon128x128.png";
import Bath from "../images/University_of_Bath_logo.png";

var contact_height = "34px";

class Bio extends Component {
	render() {
            return (
		<div id="bio" class="content-wrapper">
		    <h3>About Me</h3>
		    <p id="bio-body">I am a first year numerical analysis PhD student in Cohort 2 of the <a href="https://www.aaps-cdt.ac.uk/" target="blank">Advanced Automotive Propulsion Systems Centre for Doctoral Training</a>. Under the supervision of Dr. Tristan Pryer, I am researching numerical methods for battery modelling, with a particular focus on automotive applications. I am especially interested in structural batteries.{"\n\n"}Outside of my studies I volunteer with the ReachSci initiative, aiming to increase accessibility in academia, and I have a passion for creating music under the name <a href="http://www.eskafell.com/" target="blank">Eskafell</a>, with songs available across major streaming platforms. I also enjoy playing basketball, bouldering and generally keeping fit.{"\n\n"}My CV can be found <a href="">here</a> (coming soon).</p>
		</div>
            );
  	}
}

export default Bio;
