import React, { Component } from 'react';

class Bio extends Component {
	render() {
            return (
		<div id="bio" class="content-wrapper">
		    <h3>About Me</h3>
		    <p id="bio-body">Alex graduated from The University Of Sheffield with an MMath in Mathematics in 2019, focusing his fourth year dissertation on monomial ideals (abstract algebra). With a broad mathematical background and a passion for problem solving, Alex was excited to see how his skills could be useful in the challenges facing the automotive industry. One of the his main motivations for joining the AAPS CDT was to experience the unique trans-disciplinary nature of the programme and gain an appreciation for work in other fields. Outside of his studies Alex volunteers with the ReachSci initiative aiming to increase accessibility in academia, and he has a passion for creating music under the name Eskafell, with songs available across major streaming platforms. He also enjoys playing basketball, bouldering and generally keeping fit.</p>
		    <div id="contact-info">
		    <h3>Contact Info</h3>
		    <ul>
			<li>E: <a href="mailto:amt@bath.ac.uk">amt83@bath.ac.uk</a></li>
			<li><a href="https://www.linkedin.com/in/alex-trenam-7766981b2/" target="lank">LinkedIn</a></li>
			<li><a href="https://researchportal.bath.ac.uk/en/persons/alex-trenam" target="blank">Research Portal</a></li>
			<li><a href="https://www.aaps-cdt.ac.uk/students/alex-trenam/" target="blank">AAPS Student Page</a></li>
		    </ul>
		</div>
		</div>
            );
  	}
}

export default Bio;
