import React, { Component } from 'react';

class Bio extends Component {
	render() {
            return (
		<div id="bio" class="content-wrapper">
		    <h3>About Me</h3>
		    <p id="bio-body">I am a first year numerical analysis PhD student studying the mathematical modelling of batteries (with a focus on automotive applications), under the supervision of Dr Tristan Pryer.{"\n\n"}I am {"\n\n"}Outside of my studies, I volunteer with the ReachSci initiative aiming to increase accessibility in academia, and I have a passion for creating music under the name Eskafell, with songs available across major streaming platforms. I also enjoy playing basketball, bouldering and generally keeping fit.{"\n\n"}My CV can be found <a href="">here</a> (coming soon).</p>
		    <div id="contact-info">
			<h3>Contact Info</h3>
			<ul>
			    <li>E: <a href="mailto:amt@bath.ac.uk">amt83@bath.ac.uk</a></li>
			    <li><a href="https://www.linkedin.com/in/alex-trenam-7766981b2/" target="lank">LinkedIn</a></li>
			    <li><a href="https://researchportal.bath.ac.uk/en/persons/alex-trenam" target="blank">Research Portal</a></li>
			    <li><a href="https://www.aaps-cdt.ac.uk/students/alex-trenam/" target="blank">AAPS Student Page</a></li>
			</ul>
			<p>4W 5.08{"\n"}Dept of Mathematical Sciences{"\n"}University of Bath{"\n"}Bath{"\n"}BA2 7AY{"\n"}United Kingdom</p>
		    </div>
		</div>
            );
  	}
}

export default Bio;
